interface IYieldYakRouter {
    struct Trade {
        uint256 amountIn;
        uint256 amountOut;
        address[] path;
        address[] adapters;
    }

    struct FormattedOffer {
        uint256[] amounts;
        address[] adapters;
        address[] path;
    }

    function swapNoSplit(
        Trade calldata _trade,
        address _to,
        uint256 _fee
    ) external;
}","keccak256":"0x8b3eb66efff1b454a0860d6d08d0f6ad5208f6ff2471abfd4e2a5c582c0c2fa8","license":"BUSL-1.1"},"contracts/lib/Bytes32EnumerableMap.sol":{"content":"// SPDX-License-Identifier: MIT

