// SPDX-License-Identifier: UNLICENSED
// Last deployed from commit: ;
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../interfaces/IRatesCalculator.sol";
import "../lib/WadRayMath.sol";

/**
 * @title UtilisationRatesCalculator
 * @dev Contract which calculates the interest rates based on pool utilisation.
 * Utilisation is computed as the ratio between funds borrowed and funds deposited to the pool.
 * Rates are calculated using a linear function with slope defined by _utilisation factor and
 * shifted by an offset parameter.
 */
contract UtilisationRatesCalculator is IRatesCalculator, Ownable {
  using WadRayMath for uint256;

  uint256 utilisationFactor;
  uint256 offset;

  constructor(uint256 _utilisationFactor, uint256 _offset) {
    setParameters(_utilisationFactor, _offset);
  }

  /* ========== SETTERS ========== */

  /**
   * Sets the utilisation parameters that control rates calculation according to the linear formula:
   * Rate = utilisation * utilisationFactor + offset
   * @dev _utilisationFactor the slope of the rate formula
   * @dev _offset the offset of the rate formula
   **/
  function setParameters(uint256 _utilisationFactor, uint256 _offset) public onlyOwner {
    require(_utilisationFactor <= 1e18, "Calculator factor must not be higher than 1.");
    require(_offset <= 1e18, "Calculator offset must not be higher than 1.");

    utilisationFactor = _utilisationFactor;
    offset = _offset;

    emit ParametersUpdated(_utilisationFactor, _offset);
  }

  /* ========== VIEW FUNCTIONS ========== */

  /**
   * Returns the pool utilisation, which is a ratio between loans and deposits
   * utilisation = value_of_loans / value_of_deposits
   * @dev _totalLoans total value of loans
   * @dev _totalDeposits total value of deposits
   **/
  function getPoolUtilisation(uint256 _totalLoans, uint256 _totalDeposits) public pure returns (uint256) {
    if (_totalDeposits == 0) return 0;

    return _totalLoans.wadToRay().rayDiv(_totalDeposits.wadToRay()).rayToWad();
  }

  /**
   * Returns the current deposit rate
   * The value is based on the current borrowing rate and satisfies the invariant:
   * value_of_loans * borrowing_rate = value_of_deposits * deposit_rate
   * @dev _totalLoans total value of loans
   * @dev _totalDeposits total value of deposits
   **/
  function calculateDepositRate(uint256 _totalLoans, uint256 _totalDeposits) external view override returns (uint256) {
    if (_totalDeposits == 0) return 0;

    if (_totalLoans >= _totalDeposits) {
      return this.calculateBorrowingRate(_totalLoans, _totalDeposits);
    } else {
      return
        this.calculateBorrowingRate(_totalLoans, _totalDeposits).wadToRay().rayMul(_totalLoans.wadToRay()).rayDiv(_totalDeposits.wadToRay()).rayToWad();
    }
  }

  /**
   * Returns the current borrowing rate
   * The value is based on the pool utilisation according to the linear formula:
   * borrowing_rate = utilisation * utilisationFactor + offset
   * @dev _totalLoans total value of loans
   * @dev _totalDeposits total value of deposits
   **/
  function calculateBorrowingRate(uint256 _totalLoans, uint256 _totalDeposits) external view override returns (uint256) {
    if (_totalLoans >= _totalDeposits) {
      return utilisationFactor + offset;
    } else {
      return getPoolUtilisation(_totalLoans, _totalDeposits).wadToRay().rayMul(utilisationFactor.wadToRay()).rayToWad() + offset;
    }
  }

  /* ========== EVENTS ========== */

  /**
   * An event notifying about the parameters update.
   * @dev utilisationFactor the value of updated rate
   * @dev offset the value of updated rate
   **/
  event ParametersUpdated(uint256 utilisationFactor, uint256 offset);
}

contracts/lib/WadRayMath.sol.content:
// SPDX-License-Identifier: AGPL3
pragma solidity ^0.8.4;

/******************
@title WadRayMath library
@author Aave
@dev Provides mul and div function for wads (decimal numbers with 18 digits precision) and rays (decimals with 27 digits)
@dev https://github.com/aave/aave-protocol/blob/master/contracts/libraries/WadRayMath.sol
 */

library WadRayMath {
  uint256 internal constant WAD = 1e18;
  uint256 internal constant halfWAD = WAD / 2;

  uint256 internal constant RAY = 1e27;
  uint256 internal constant halfRAY = RAY / 2;

  uint256 internal constant WAD_RAY_RATIO = 1e9;

  function ray() internal pure returns (uint256) {
    return RAY;
  }

  function wad() internal pure returns (uint256) {
    return WAD;
  }

  function halfRay() internal pure returns (uint256) {
    return halfRAY;
  }

  function halfWad() internal pure returns (uint256) {
    return halfWAD;
  }

  function wadMul(uint256 a, uint256 b) internal pure returns (uint256) {
    //return halfWAD.add(a.mul(b)).div(WAD);
    return (halfWAD + (a * b)) / WAD;
  }

  function wadDiv(uint256 a, uint256 b) internal pure returns (uint256) {
    uint256 halfB = b / 2;

    //return halfB.add(a.mul(WAD)).div(b);
    return (halfB + (a * WAD)) / b;
  }

  function rayMul(uint256 a, uint256 b) internal pure returns (uint256) {
    //return halfRAY.add(a.mul(b)).div(RAY);
    return (halfRAY + (a * b)) / RAY;
  }

  function rayDiv(uint256 a, uint256 b) internal pure returns (uint256) {
    uint256 halfB = b / 2;

    //return halfB.add(a.mul(RAY)).div(b);
    return (halfB + (a * RAY)) / b;
  }

  function rayToWad(uint256 a) internal pure returns (uint256) {
    uint256 halfRatio = WAD_RAY_RATIO / 2;

    //return halfRatio.add(a).div(WAD_RAY_RATIO);
    return (halfRatio + a) / WAD_RAY_RATIO;
  }

  function wadToRay(uint256 a) internal pure returns (uint256) {
    //return a.mul(WAD_RAY_RATIO);
    return a * WAD_RAY_RATIO;
  }

  /**
   * @dev calculates base^exp. The code uses the ModExp precompile
   */
  //solium-disable-next-line
  function rayPow(uint256 x, uint256 n) internal pure returns (uint256 z) {
    z = n % 2 != 0 ? x : RAY;

    for (n /= 2; n != 0; n /= 2) {
      x = rayMul(x, x);

      if (n % 2 != 0) {
        z = rayMul(z, x);
      }
    }
  }
}

contracts/mock/CompoundingIndexDebug.sol.content:
// SPDX-License-Identifier: UNLICENSED
// Last deployed from commit: ;
pragma solidity ^0.8.4;

import "../lib/WadRayMath.sol";

import "hardhat/console.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract CompoundingIndexDebug is Ownable {
  using WadRayMath for uint256;

  uint256 private constant SECONDS_IN_YEAR = 365 days;
  uint256 private constant BASE_RATE = 1e18;

  uint256 public start = block.timestamp;

  uint256 public index = BASE_RATE;
  uint256 public indexUpdateTime = start;

  mapping(uint256 => uint256) prevIndex;
  mapping(address => uint256) userUpdateTime;

  uint256 public rate;

  function setRate(uint256 _rate) public onlyOwner {
    updateIndex();
    rate = _rate;
  }

  function updateUser(address user) public onlyOwner {
    userUpdateTime[user] = block.timestamp;
    prevIndex[block.timestamp] = getIndex();

    //console.log("Updated at: ", now);
    //console.log("Updated index to: ", prevIndex[now]);
  }

  function getIndexedValue(uint256 value, address user) public view returns (uint256) {
    //uint256 lastIndex = prevIndex[getLastUserUpdateTime(user)];
    //console.log("Last time: ", getLastUserUpdateTime(user));
    //console.log("Last index: ", lastIndex);
    //console.log("Start time: ", start);

    return value.wadToRay().rayMul(getIndex().wadToRay()).rayDiv(prevIndex[getLastUserUpdateTime(user)].wadToRay()).rayToWad();
  }

  function updateIndex() internal {
    prevIndex[indexUpdateTime] = index;

    //console.log("Updated at: ", indexUpdateTime);
    //console.log("Updated index to: ", index);

    index = getIndex();
    indexUpdateTime = block.timestamp;
  }

  function getLastUserUpdateTime(address user) internal view returns (uint256) {
    return userUpdateTime[user] == 0 ? start : userUpdateTime[user];
  }

  function getCompoundedFactor(uint256 period) internal view returns (uint256) {
    return ((rate.wadToRay() / SECONDS_IN_YEAR) + WadRayMath.ray()).rayPow(period);
  }

  function getIndex() public view returns (uint256) {
    uint256 period = block.timestamp - indexUpdateTime;
    if (period > 0) {
      return index.wadToRay().rayMul(getCompoundedFactor(period)).rayToWad();
    } else {
      return index;
    }
  }
}

