const { ethers } = require('ethers');

// Define the ABI for the contract
const abi = require('../../artifacts/contracts/facets/DiamondCutFacet.sol/DiamondCutFacet.json').abi;

// Create an ethers Interface instance
const iface = new ethers.utils.Interface(abi);

// Define the calldata to be decoded
const calldata = "";

// Decode the calldata
const decodedData = iface.decodeFunctionData("diamondCut", calldata);

// Print the decoded data
console.log(JSON.stringify(decodedData, null, 2));